import React, { useState, useEffect } from "react";
import "./OurModels.scss";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import { Link } from "react-router-dom";

const OurModels = () => {
  const [models, setModels] = useState([]); // Состояние для моделей
  const [loading, setLoading] = useState(true); // Индикатор загрузки
  const [error, setError] = useState(null); // Ошибка запроса
  const [showMore, setShowMore] = useState(false); // Показывать больше моделей

  // Функция загрузки моделей с сервера
  useEffect(() => {
    const fetchModels = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/models/main/");
        if (!response.ok) throw new Error("Failed to fetch models");

        const data = await response.json();
        setModels(data.results); // Устанавливаем модели из API
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchModels();
  }, []);

  return (
    <>
      <Header />

      <main className="main">
        <section className="our-models">
          <div className="our-models__title">
            <h2 className="our-models__title-text">
              Connect with our top models worldwide. Effortless talent search
              <br /> to bring your vision to life.
            </h2>
          </div>

          {loading && <p>Loading models...</p>}
          {error && <p className="error">{error}</p>}

          <div className="our-models__block">
            {models.slice(0, showMore ? models.length : 6).map((model) => (
              <Link to={`/models/${model.id}`} key={model.id} className="our-models__link">
                <div className="our-models__card">
                  <div className="our-models__img">
                    <img src={model.image_url} alt={model.full_name} />
                  </div>
                  <div className="our-models__info">
                    <div className="our-models__name">{model.full_name}</div>
                    <div className="our-models__location">
                      {model.city}, {model.country}
                    </div>
                  </div>
                </div>
              </Link>
            ))}
          </div>

          {!showMore && models.length > 6 && (
            <div className="our-models__btn">
              <div className="our-models__btn-show" onClick={() => setShowMore(true)}>
                Show more
              </div>
            </div>
          )}
        </section>
      </main>

      <Footer />
    </>
  );
};

export default OurModels;
