import React, { useState } from "react";
import "../../App.scss";
import TopBar from "../../components/TopBar/TopBar";
import Footer from "../../components/Footer/Footer";
import "./Contact.scss";

const Contact = () => {
// Используем React-хук useState для хранения данных формы
const [formData, setFormData] = useState({
  name: "",           // Имя пользователя
  last_name: "",      // Фамилия пользователя
  email: "",          // Электронная почта
  phone_number: "",   // Номер телефона
  message: "",        // Сообщение от пользователя
  agreement: false,   // Флажок согласия на обработку данных
});

// Функция для обновления данных формы при вводе
const handleChange = (e) => {
  const { name, value, type, checked } = e.target; // Получаем имя поля, введённое значение и его тип
  setFormData({
    ...formData, // Копируем текущие данные
    [name]: type === "checkbox" ? checked : value, // Если это чекбокс — используем checked, иначе значение input
  });
};

// Функция, которая срабатывает при отправке формы
const handleSubmit = async (e) => {
  e.preventDefault(); // Предотвращаем перезагрузку страницы

  try {
    // Отправляем POST-запрос на сервер с данными формы
    const response = await fetch("http://127.0.0.1:8000/contact/", {
      method: "POST", // Указываем, что отправляем данные
      headers: {
        "Content-Type": "application/json", // Данные в формате JSON
      },
      body: JSON.stringify(formData), // Преобразуем объект в строку JSON
    });

    // Пытаемся распарсить ответ сервера (если сервер ответит не JSON, пропускаем ошибку)
    // const result = await response.json().catch(() => null);

    if (response.ok) {
      // Если сервер ответил успешно (код 200), показываем сообщение об успехе
      alert("Form submitted successfully!");
      setFormData({
        name: "",
        last_name: "",
        email: "",
        phone_number: "",
        message: "",
        agreement: false,
      }); // Очищаем форму после успешной отправки
    } else {
      // Если сервер вернул ошибку, выводим сообщение с кодом ошибки
      console.error("Request error:", result);
      alert(`Error: ${response.status} ${result ? JSON.stringify(result) : ""}`);
    }
  } catch (error) {
    // Если запрос не удался (например, сервер не запущен), выводим сообщение об ошибке сети
    console.error("Network error:", error);
    alert("Network error. Please check your server connection.");
  }
};


  return (
    <>
      <div className="contact-page">
        <TopBar />
      </div>

      <main className="main main-contact">
        <section className="contact">
          <h1 className="contact-title">Contact us</h1>

          <div className="contact-content">
            <div className="contact-content__form">
              <form autoComplete="on" id="form" method="post" onSubmit={handleSubmit} className="contact__message-form">
                <div className="contact__names">
                  <div className="user-name">
                    <label htmlFor="name" className="form__label">Name*</label>
                    <input
                      type="text"
                      id="name"
                      className="form__input form__input--name form__input-item"
                      name="name"
                      placeholder="Enter your name"
                      required
                      value={formData.name}
                      onChange={handleChange}
                    />
                  </div>

                  <div className="company-name">
                    <label htmlFor="last_name" className="form__label">Last name*</label>
                    <input
                      type="text"
                      id="last_name"
                      className="form__input form__input--companyName form__input-item"
                      name="last_name"
                      placeholder="Enter your last name"
                      required
                      value={formData.last_name}
                      onChange={handleChange}
                    />
                  </div>
                </div>

                <div className="contact__info">
                  <div className="contact__email">
                    <label htmlFor="email" className="form__label">Email*</label>
                    <input
                      id="email"
                      className="form__input form__input--email"
                      type="email"
                      name="email"
                      placeholder="Enter your email"
                      required
                      value={formData.email}
                      onChange={handleChange}
                    />
                  </div>

                  <div className="contact__phone">
                    <label htmlFor="phone_number" className="form__label">Phone number*</label>
                    <input
                      id="phone_number"
                      className="form__input form__input--phone form__input-item"
                      type="tel"
                      name="phone_number"
                      placeholder="Enter your phone number"
                      required
                      value={formData.phone_number}
                      onChange={handleChange}
                    />
                  </div>
                </div>

                <div className="contact__message form__control">
                  <label htmlFor="message" className="form__label">Message*</label>
                  <textarea
                    name="message"
                    className="form__input input__textarea"
                    placeholder="Enter your message"
                    required
                    value={formData.message}
                    onChange={handleChange}
                  ></textarea>
                </div>

                <div className="contact__sent">
                  <div className="contact__policy">
                    <div className="contact-us__policy--text">
                      I agree to receive information from you by email regarding your products and services*
                    </div>

                    <div className="contact__checkbox">
                      <label htmlFor="checkbox" className="contact__checkbox-label">
                        <input
                          className="check-box"
                          type="checkbox"
                          required
                          id="checkbox"
                          name="agreement"
                          checked={formData.agreement}
                          onChange={handleChange}
                        />
                        <span className="contact__checkbox-text">I agree</span>
                      </label>
                    </div>
                  </div>

                  <div className="contact__button">
                    <button className="contact__text" type="submit">SUBMIT</button>
                  </div>
                </div>
              </form>
            </div>

            <div className="contact-content__information">
              <div className="contact-content__information-photo">
                <img
                  src="./images/contact/contact-photo.png"
                  alt="contact-photo"
                />
              </div>
              <div className="contact-content__background"></div>
              <div className="contact-content__information-wrapper">
                <div className="contact-content__information-title">Axiom</div>
                <div className="contact-content__information-discription">
                  It is very important for us to keep in touch with you, so we
                  are always ready to answer any question that interests you.
                  Shoot!
                </div>
              </div>
            </div>
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Contact;
