import React, { useState } from "react";
import TopBar from "../../components/TopBar/TopBar";
import Footer from "../../components/Footer/Footer";
import "./Newsletter.scss";
import { Link } from "react-router-dom";

const Newsletter = () => {
  const [email, setEmail] = useState(""); // Храним email
  const [message, setMessage] = useState(""); // Храним сообщение о статусе запроса

  // Функция валидации email
  const isValidEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  // Функция отправки email на сервер
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!isValidEmail(email)) {
      setMessage("Please enter a valid email address.");
      return;
    }

    try {
      const response = await fetch("http://127.0.0.1:8000/newsletters/sign/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      if (response.ok) {
        setMessage("Successfully subscribed to the newsletter!");
        setEmail(""); // Очищаем поле ввода после успешной подписки
      } else {
        setMessage("Subscription failed. Please try again.");
      }
    } catch (error) {
      setMessage("Network error. Please check your connection.");
    }
  };

  return (
    <>
      <div className="contact-page">
        <TopBar />
      </div>

      <main className="main main-contact">
        <section className="newsletter">
          <div className="newsletter__wrapper">
            <h1 className="newsletter__title">Newsletter</h1>
            <p className="newsletter__discription">
              Sign up for newsletters below. Just tell us which newsletters you
              want to receive and where to send them.
            </p>
          </div>

          <form className="newsletter__message-form" onSubmit={handleSubmit}>
            <div className="newsletter__main">
              <div className="newsletter__main-left">
                {/* Чекбоксы выбора рассылки */}
                <div className="newsletter__items">
                  {["Personalized Recommendations", "ModelVibe", "Trending on ModelVibe"].map((title, index) => (
                    <div className="newsletter__item" key={index}>
                      <label className="newsletter__checkbox-label">
                        <input type="checkbox" className="check-box check-box__newsletter" />
                        <span className="newsletter__checkbox-title">{title}</span>
                      </label>
                    </div>
                  ))}
                </div>
              </div>

              <div className="newsletter__main-line"></div>

              <div className="newsletter__main-right">
                <div className="contact__email">
                  <label htmlFor="user-email" className="form__label form__label-newsletter">Email</label>
                  <input
                    id="user-email"
                    className="form__input form__input--email"
                    type="email"
                    name="email"
                    placeholder="Enter your email"
                    required
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>

                {message && <p className="newsletter__message">{message}</p>}

                <div className="newsletter__button">
                  <button className="newsletter__text" type="submit">SUBMIT</button>
                </div>
              </div>
            </div>
          </form>
        </section>
      </main>

      <Footer />
    </>
  );
};

export default Newsletter;
